                    <span>₹{cartTotal}</span>
                  </div>
                </div>
                <button onClick={() => setCurrentPage('checkout')} disabled={orderLoading} className="bg-orange-500 text-white w-full py-3 rounded font-bold hover:bg-orange-600 mb-2">{orderLoading ? '⏳ Processing...' : '💳 Checkout'}</button>
                <button onClick={() => setCurrentPage('categories')} className="bg-gray-300 text-gray-800 w-full py-2 rounded font-bold hover:bg-gray-400">Continue Shopping</button>
              </div>
            </div>
          )}
        </div>
      </div>
    );
