import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // 'login', 'signup', 'shop', 'admin'
  const [user, setUser] = useState(null);
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', price: '', image: '', category: '', stock: '' });
  const [loading, setLoading] = useState(false);

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:5000/api/auth/profile', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => {
          setUser(res.data.user);
          setCurrentPage('shop');
          fetchProducts();
        })
        .catch(() => {
          localStorage.removeItem('token');
          setCurrentPage('login');
        });
    }
  }, []);

  const fetchProducts = () => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(err => console.error("Error:", err));
  };

  // ===== SIGNUP =====
  const handleSignup = async (e) => {
    e.preventDefault();
    const email = document.getElementById('signup-email').value;
    const name = document.getElementById('signup-name').value;
    const password = document.getElementById('signup-password').value;
    const confirmPassword = document.getElementById('signup-confirm').value;

    if (password !== confirmPassword) {
      alert("‚ö†Ô∏è Passwords match nahi hai!");
      return;
    }

    setLoading(true);
    axios.post('http://localhost:5000/api/auth/signup', { name, email, password })
      .then(res => {
        alert("‚úÖ Signup Successful! Ab login karo!");
        localStorage.setItem('token', res.data.token);
        setUser(res.data.user);
        setCurrentPage('shop');
        fetchProducts();
      })
      .catch(err => alert("‚ùå Error: " + (err.response?.data?.error || err.message)))
      .finally(() => setLoading(false));
  };

  // ===== LOGIN =====
  const handleLogin = async (e) => {
    e.preventDefault();
    const email = document.getElementById('login-email').value;
    const password = document.getElementById('login-password').value;

    setLoading(true);
    axios.post('http://localhost:5000/api/auth/login', { email, password })
      .then(res => {
        alert("‚úÖ Login Successful!");
        localStorage.setItem('token', res.data.token);
        setUser(res.data.user);
        setCurrentPage('shop');
        fetchProducts();
      })
      .catch(err => alert("‚ùå Error: " + (err.response?.data?.error || err.message)))
      .finally(() => setLoading(false));
  };

  // LOGOUT
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setProducts([]);
    setCurrentPage('login');
  };

  // Add Product
  const handleAdd = () => {
    if (!form.name || !form.price || !form.image) {
      alert("‚ö†Ô∏è Name, Price aur Image required hain!");
      return;
    }

    setLoading(true);
    const token = localStorage.getItem('token');
    axios.post('http://localhost:5000/api/products', form, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(() => {
        alert("‚úÖ Product Added!");
        setForm({ name: '', price: '', image: '', category: '', stock: '' });
        fetchProducts();
      })
      .catch(err => alert("‚ùå Error: " + err.message))
      .finally(() => setLoading(false));
  };

  // Delete Product
  const handleDelete = (id) => {
    if (window.confirm("Kya delete karna chahte ho?")) {
      const token = localStorage.getItem('token');
      axios.delete(`http://localhost:5000/api/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(() => {
          alert("‚úÖ Deleted!");
          fetchProducts();
        })
        .catch(err => alert("‚ùå Error: " + err.message));
    }
  };

  // ===== LOGIN PAGE =====
  if (currentPage === 'login') {
    return (
      <div className="min-h-screen bg-gradient-to-r from-blue-600 to-blue-800 flex items-center justify-center p-4">
        <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md">
          <h1 className="text-4xl font-bold text-center mb-8 text-blue-600">Flipzokart</h1>
          
          <form onSubmit={handleLogin} className="space-y-4">
            <h2 className="text-2xl font-bold mb-6 text-center">üîê Login</h2>
            <input
              id="login-email"
              type="email"
              placeholder="üìß Email"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
              required
            />
            <input
              id="login-password"
              type="password"
              placeholder="üîë Password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
              required
            />
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-blue-600 text-white py-3 font-bold rounded hover:bg-blue-700 disabled:bg-gray-500"
            >
              {loading ? "‚è≥ Loading..." : "‚úÖ Login"}
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-gray-600">Account nahi hai?</p>
            <button
              onClick={() => setCurrentPage('signup')}
              className="text-blue-600 font-bold hover:underline mt-2"
            >
              Signup karo üëâ
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ===== SIGNUP PAGE =====
  if (currentPage === 'signup') {
    return (
      <div className="min-h-screen bg-gradient-to-r from-green-600 to-green-800 flex items-center justify-center p-4">
        <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md">
          <h1 className="text-4xl font-bold text-center mb-8 text-green-600">Flipzokart</h1>
          
          <form onSubmit={handleSignup} className="space-y-4">
            <h2 className="text-2xl font-bold mb-6 text-center">üìù Sign Up</h2>
            <input
              id="signup-name"
              type="text"
              placeholder="üë§ Full Name"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <input
              id="signup-email"
              type="email"
              placeholder="üìß Email"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <input
              id="signup-password"
              type="password"
              placeholder="üîë Password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <input
              id="signup-confirm"
              type="password"
              placeholder="üîê Confirm Password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-green-600 text-white py-3 font-bold rounded hover:bg-green-700 disabled:bg-gray-500"
            >
              {loading ? "‚è≥ Loading..." : "‚úÖ Sign Up"}
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-gray-600">Pehle se account hai?</p>
            <button
              onClick={() => setCurrentPage('login')}
              className="text-green-600 font-bold hover:underline mt-2"
            >
              Login karo üëâ
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ===== SHOP PAGE =====
  if (currentPage === 'shop') {
    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        {/* NAVBAR */}
        <nav className="bg-blue-600 p-4 text-white flex justify-between px-10 items-center shadow-lg">
          <h1 className="text-3xl font-bold italic">Flipzokart</h1>
          <div className="space-x-4 flex items-center">
            <span className="text-sm">üëã {user?.name}</span>
            <button
              onClick={() => setCurrentPage('shop')}
              className={`px-4 py-2 rounded font-bold ${currentPage === 'shop' ? 'bg-white text-blue-600' : 'hover:bg-blue-700'}`}
            >
              üõçÔ∏è Shop
            </button>
            {user?.role === 'admin' && (
              <button
                onClick={() => setCurrentPage('admin')}
                className={`px-4 py-2 rounded font-bold ${currentPage === 'admin' ? 'bg-white text-blue-600' : 'hover:bg-blue-700'}`}
              >
                ‚öôÔ∏è Admin
              </button>
            )}
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-2 rounded font-bold hover:bg-red-600"
            >
              üö™ Logout
            </button>
          </div>
        </nav>

        {/* SHOP VIEW */}
        <div className="p-10">
          <h2 className="text-3xl font-bold mb-6">üéâ Welcome to Flipzokart</h2>
          {products.length === 0 ? (
            <p className="text-center text-gray-500 text-xl">‚ùå Koi products nahi hain yet</p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {products.map(p => (
                <div key={p._id} className="bg-white p-4 rounded shadow-md hover:shadow-xl transition">
                  <img src={p.image} className="h-48 w-full object-contain rounded" alt={p.name} />
                  <h2 className="font-bold mt-3 text-lg truncate">{p.name}</h2>
                  <p className="text-gray-500 text-sm">{p.category || 'General'}</p>
                  <p className="text-green-600 font-bold text-xl mt-2">‚Çπ{p.price}</p>
                  <p className="text-sm text-gray-600">Stock: {p.stock || 'Available'}</p>
                  <button className="bg-orange-500 text-white w-full py-2 mt-3 rounded font-bold hover:bg-orange-600">
                    üõí Add to Cart
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

  // ===== ADMIN PAGE =====
  if (currentPage === 'admin') {
    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        {/* NAVBAR */}
        <nav className="bg-blue-600 p-4 text-white flex justify-between px-10 items-center shadow-lg">
          <h1 className="text-3xl font-bold italic">Flipzokart</h1>
          <div className="space-x-4 flex items-center">
            <span className="text-sm">üëã {user?.name}</span>
            <button
              onClick={() => setCurrentPage('shop')}
              className="px-4 py-2 rounded font-bold hover:bg-blue-700"
            >
              üõçÔ∏è Shop
            </button>
            <button
              onClick={() => setCurrentPage('admin')}
              className="bg-white text-blue-600 px-4 py-2 rounded font-bold"
            >
              ‚öôÔ∏è Admin
            </button>
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-2 rounded font-bold hover:bg-red-600"
            >
              üö™ Logout
            </button>
          </div>
        </nav>

        {/* ADMIN CONTENT */}
        <div className="p-10 flex flex-col lg:flex-row gap-10">
          {/* Add Product Form */}
          <div className="max-w-lg bg-white p-6 shadow-lg rounded-lg">
            <h2 className="text-2xl font-bold mb-6">‚ûï Add New Product</h2>
            <div className="space-y-4">
              <input
                placeholder="Product Name"
                className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
                value={form.name}
                onChange={e => setForm({...form, name: e.target.value})}
              />
              <input
                placeholder="Price (‚Çπ)"
                type="number"
                className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
                value={form.price}
                onChange={e => setForm({...form, price: e.target.value})}
              />
              <input
                placeholder="Image URL"
                className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
                value={form.image}
                onChange={e => setForm({...form, image: e.target.value})}
              />
              <input
                placeholder="Category"
                className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
                value={form.category}
                onChange={e => setForm({...form, category: e.target.value})}
              />
              <input
                placeholder="Stock"
                type="number"
                className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
                value={form.stock}
                onChange={e => setForm({...form, stock: e.target.value})}
              />
              <button
                onClick={handleAdd}
                disabled={loading}
                className="bg-blue-600 text-white w-full py-3 font-bold rounded hover:bg-blue-700 disabled:bg-gray-500"
              >
                {loading ? "‚è≥ Adding..." : "‚úÖ UPLOAD PRODUCT"}
              </button>
            </div>
          </div>

          {/* Products List */}
          <div className="flex-1">
            <h2 className="text-2xl font-bold mb-6">üì¶ All Products ({products.length})</h2>
            {products.length === 0 ? (
              <p className="text-gray-500 text-lg">Koi products nahi hain</p>
            ) : (
              <div className="grid grid-cols-1 gap-4">
                {products.map(p => (
                  <div key={p._id} className="bg-white p-4 rounded shadow-md flex justify-between items-center">
                    <div className="flex-1">
                      <h3 className="font-bold text-lg">{p.name}</h3>
                      <p className="text-green-600 font-bold">‚Çπ{p.price}</p>
                      <p className="text-sm text-gray-600">{p.category || 'General'} | Stock: {p.stock || 'N/A'}</p>
                    </div>
                    <button
                      onClick={() => handleDelete(p._id)}
                      className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 font-bold"
                    >
                      üóëÔ∏è Delete
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
