import React, { useState } from 'react';

export default function CheckoutForm({ cart, cartTotal, onPlaceOrder, loading }) {
  const [shipping, setShipping] = useState({
    name: '', phone: '', address: '', city: '', pincode: '', locality: '', landmark: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!shipping.name || !shipping.phone || !shipping.address || !shipping.city || !shipping.pincode || !shipping.locality || !shipping.landmark) {
      alert('Please fill all shipping details');
      return;
    }
    onPlaceOrder(shipping);
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md max-w-lg mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">üöö Shipping Details</h2>
      <input placeholder="Name" className="w-full p-2 mb-2 border rounded" value={shipping.name} onChange={e => setShipping({ ...shipping, name: e.target.value })} />
      <input placeholder="Phone" className="w-full p-2 mb-2 border rounded" value={shipping.phone} onChange={e => setShipping({ ...shipping, phone: e.target.value })} />
      <input placeholder="Address" className="w-full p-2 mb-2 border rounded" value={shipping.address} onChange={e => setShipping({ ...shipping, address: e.target.value })} />
      <input placeholder="City" className="w-full p-2 mb-2 border rounded" value={shipping.city} onChange={e => setShipping({ ...shipping, city: e.target.value })} />
      <input placeholder="Pincode" className="w-full p-2 mb-2 border rounded" value={shipping.pincode} onChange={e => setShipping({ ...shipping, pincode: e.target.value })} />
      <input placeholder="Locality" className="w-full p-2 mb-2 border rounded" value={shipping.locality} onChange={e => setShipping({ ...shipping, locality: e.target.value })} />
      <input placeholder="Landmark" className="w-full p-2 mb-2 border rounded" value={shipping.landmark} onChange={e => setShipping({ ...shipping, landmark: e.target.value })} />
      <div className="mt-4">
        <h3 className="text-xl font-bold mb-2">Order Summary</h3>
        {cart.map(item => (
          <div key={item._id} className="flex justify-between text-sm mb-1">
            <span>{item.name} x {item.quantity}</span>
            <span>‚Çπ{item.price * item.quantity}</span>
          </div>
        ))}
        <div className="font-bold flex justify-between mt-2">
          <span>Total:</span>
          <span>‚Çπ{cartTotal}</span>
        </div>
      </div>
      <button type="submit" disabled={loading} className="w-full bg-blue-600 text-white py-3 font-bold rounded hover:bg-blue-700 disabled:bg-gray-500 mt-4">
        {loading ? '‚è≥ Placing Order...' : '‚úÖ Place Order'}
      </button>
    </form>
  );
}
