import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // 'login', 'signup', 'home', 'shop', 'categories', 'cart', 'account', 'admin'
  const [user, setUser] = useState(null);
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [form, setForm] = useState({ name: '', price: '', image: '', category: '', stock: '' });
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:5000/api/auth/profile', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => {
          setUser(res.data.user);
          setCurrentPage('home');
          fetchProducts();
        })
        .catch(() => {
          localStorage.removeItem('token');
          setCurrentPage('login');
        });
    }
  }, []);

  const fetchProducts = () => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(err => console.error("Error:", err));
  };

  // ===== SIGNUP =====
  const handleSignup = async (e) => {
    e.preventDefault();
    const email = document.getElementById('signup-email').value;
    const name = document.getElementById('signup-name').value;
    const password = document.getElementById('signup-password').value;
    const confirmPassword = document.getElementById('signup-confirm').value;

    if (password !== confirmPassword) {
      alert("⚠️ Passwords match nahi hai!");
      return;
    }

    setLoading(true);
    axios.post('http://localhost:5000/api/auth/signup', { name, email, password })
      .then(res => {
        alert("✅ Signup Successful! Ab login karo!");
        localStorage.setItem('token', res.data.token);
        setUser(res.data.user);
        setCurrentPage('shop');
        fetchProducts();
      })
      .catch(err => alert("❌ Error: " + (err.response?.data?.error || err.message)))
      .finally(() => setLoading(false));
  };

  // ===== LOGIN =====
  const handleLogin = async (e) => {
    e.preventDefault();
    const email = document.getElementById('login-email').value;
    const password = document.getElementById('login-password').value;

    setLoading(true);
    axios.post('http://localhost:5000/api/auth/login', { email, password })
      .then(res => {
        alert("✅ Login Successful!");
        localStorage.setItem('token', res.data.token);
        setUser(res.data.user);
        setCurrentPage('shop');
        fetchProducts();
      })
      .catch(err => alert("❌ Error: " + (err.response?.data?.error || err.message)))
      .finally(() => setLoading(false));
  };

  // LOGOUT
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setProducts([]);
    setCart([]);
    setCurrentPage('login');
  };

  // ADD TO CART
  const handleAddToCart = (product) => {
    const existingItem = cart.find(item => item._id === product._id);
    if (existingItem) {
      setCart(cart.map(item =>
        item._id === product._id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
    alert("✅ Product added to cart!");
  };

  // REMOVE FROM CART
  const handleRemoveFromCart = (productId) => {
    setCart(cart.filter(item => item._id !== productId));
  };

  // UPDATE CART QUANTITY
  const handleUpdateQuantity = (productId, quantity) => {
    if (quantity <= 0) {
      handleRemoveFromCart(productId);
    } else {
      setCart(cart.map(item =>
        item._id === productId ? { ...item, quantity } : item
      ));
    }
  };

  // CALCULATE TOTAL
  const cartTotal = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  // Add Product
  const handleAdd = () => {
    if (!form.name || !form.price || !form.image) {
      alert("⚠️ Name, Price aur Image required hain!");
      return;
    }

    setLoading(true);
    const token = localStorage.getItem('token');
    axios.post('http://localhost:5000/api/products', form, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(() => {
        alert("✅ Product Added!");
        setForm({ name: '', price: '', image: '', category: '', stock: '' });
        fetchProducts();
      })
      .catch(err => alert("❌ Error: " + err.message))
      .finally(() => setLoading(false));
  };

  // Delete Product
  const handleDelete = (id) => {
    if (window.confirm("Kya delete karna chahte ho?")) {
      const token = localStorage.getItem('token');
      axios.delete(`http://localhost:5000/api/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(() => {
          alert("✅ Deleted!");
          fetchProducts();
        })
        .catch(err => alert("❌ Error: " + err.message));
    }
  };

  // ===== LOGIN PAGE =====
  if (currentPage === 'login') {
    return (
      <div className="min-h-screen bg-gradient-to-r from-blue-600 to-blue-800 flex items-center justify-center p-4">
        <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md">
          <h1 className="text-4xl font-bold text-center mb-8 text-blue-600">Flipzokart</h1>
          
          <form onSubmit={handleLogin} className="space-y-4">
            <h2 className="text-2xl font-bold mb-6 text-center">🔐 Login</h2>
            <input
              id="login-email"
              type="email"
              placeholder="📧 Email"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
              required
            />
            <input
              id="login-password"
              type="password"
              placeholder="🔑 Password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600"
              required
            />
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-blue-600 text-white py-3 font-bold rounded hover:bg-blue-700 disabled:bg-gray-500"
            >
              {loading ? "⏳ Loading..." : "✅ Login"}
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-gray-600">Account nahi hai?</p>
            <button
              onClick={() => setCurrentPage('signup')}
              className="text-blue-600 font-bold hover:underline mt-2"
            >
              Signup karo 👉
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ===== SIGNUP PAGE =====
  if (currentPage === 'signup') {
    return (
      <div className="min-h-screen bg-gradient-to-r from-green-600 to-green-800 flex items-center justify-center p-4">
        <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md">
          <h1 className="text-4xl font-bold text-center mb-8 text-green-600">Flipzokart</h1>
          
          <form onSubmit={handleSignup} className="space-y-4">
            <h2 className="text-2xl font-bold mb-6 text-center">📝 Sign Up</h2>
            <input
              id="signup-name"
              type="text"
              placeholder="👤 Full Name"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <input
              id="signup-email"
              type="email"
              placeholder="📧 Email"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <input
              id="signup-password"
              type="password"
              placeholder="🔑 Password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <input
              id="signup-confirm"
              type="password"
              placeholder="🔐 Confirm Password"
              className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-green-600"
              required
            />
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-green-600 text-white py-3 font-bold rounded hover:bg-green-700 disabled:bg-gray-500"
            >
              {loading ? "⏳ Loading..." : "✅ Sign Up"}
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-gray-600">Pehle se account hai?</p>
            <button
              onClick={() => setCurrentPage('login')}
              className="text-green-600 font-bold hover:underline mt-2"
            >
              Login karo 👉
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ===== NAVBAR COMPONENT =====
  const Navbar = () => (
    <nav className="bg-blue-600 p-4 text-white flex justify-between px-6 items-center shadow-lg sticky top-0 z-50">
      <div className="flex items-center space-x-3">
        <div className="bg-white text-blue-600 rounded-full w-12 h-12 flex items-center justify-center font-bold text-2xl">F</div>
        <h1 className="text-3xl font-bold italic">Flipzokart</h1>
      </div>
      
      <div className="space-x-2 flex items-center">
        <button onClick={() => setCurrentPage('home')} className={`px-4 py-2 rounded font-bold ${currentPage === 'home' ? 'bg-white text-blue-600' : 'hover:bg-blue-700'}`}>🏠 Home</button>
        <button onClick={() => setCurrentPage('categories')} className={`px-4 py-2 rounded font-bold ${currentPage === 'categories' ? 'bg-white text-blue-600' : 'hover:bg-blue-700'}`}>📂 Categories</button>
        <button onClick={() => setCurrentPage('cart')} className={`px-4 py-2 rounded font-bold relative ${currentPage === 'cart' ? 'bg-white text-blue-600' : 'hover:bg-blue-700'}`}>
          🛒 Cart
          {cart.length > 0 && <span className="absolute top-0 right-0 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs">{cart.length}</span>}
        </button>
        <button onClick={() => setCurrentPage('account')} className={`px-4 py-2 rounded font-bold ${currentPage === 'account' ? 'bg-white text-blue-600' : 'hover:bg-blue-700'}`}>👤 Account</button>
        {user?.role === 'admin' && <button onClick={() => setCurrentPage('admin')} className={`px-4 py-2 rounded font-bold ${currentPage === 'admin' ? 'bg-white text-blue-600' : 'hover:bg-blue-700'}`}>⚙️ Admin</button>}
        <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded font-bold hover:bg-red-600">🚪 Logout</button>
      </div>
    </nav>
  );

  // ===== HOME PAGE =====
  if (currentPage === 'home') {
    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        <Navbar />
        
        {/* HERO BANNER */}
        <div className="bg-gradient-to-r from-blue-600 via-blue-500 to-orange-500 text-white">
          <div className="max-w-7xl mx-auto px-6 py-24 flex items-center justify-between">
            <div className="flex-1">
              <h1 className="text-6xl font-bold mb-4 leading-tight">
                🎉 Apka Number 1 <br /> E-Commerce Platform
              </h1>
              <p className="text-xl mb-2 opacity-90">Best products, Best prices, Best service!</p>
              <p className="text-lg mb-8 opacity-80">Lakh+ satisfied customers trust Flipzokart</p>
              
              <div className="flex space-x-4">
                <button 
                  onClick={() => setCurrentPage('categories')}
                  className="bg-white text-blue-600 px-8 py-3 rounded-lg font-bold text-lg hover:bg-gray-100 shadow-lg"
                >
                  🛍️ Start Shopping
                </button>
                <button 
                  onClick={() => setCurrentPage('categories')}
                  className="border-2 border-white text-white px-8 py-3 rounded-lg font-bold text-lg hover:bg-white hover:text-blue-600"
                >
                  📂 Explore Categories
                </button>
              </div>
              
              <div className="flex space-x-8 mt-12">
                <div>
                  <p className="text-3xl font-bold">10K+</p>
                  <p className="text-sm opacity-90">Products</p>
                </div>
                <div>
                  <p className="text-3xl font-bold">100K+</p>
                  <p className="text-sm opacity-90">Customers</p>
                </div>
                <div>
                  <p className="text-3xl font-bold">24/7</p>
                  <p className="text-sm opacity-90">Support</p>
                </div>
              </div>
            </div>
            
            <div className="flex-1 text-center">
              <div className="text-9xl">🛒</div>
              <p className="text-2xl font-bold mt-4">Welcome to Flipzokart!</p>
              <p className="text-lg mt-2 opacity-90">👋 {user?.name}, Shop with confidence</p>
            </div>
          </div>
        </div>
        
        <div className="p-10">
          {/* FEATURES SECTION */}
          <div className="mb-10">
            <h2 className="text-3xl font-bold mb-6 text-center">✨ Why Choose Flipzokart?</h2>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl text-center">
                <p className="text-5xl mb-3">🚚</p>
                <h3 className="text-xl font-bold mb-2">Fast Delivery</h3>
                <p className="text-gray-600">Quick shipping to your doorstep</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl text-center">
                <p className="text-5xl mb-3">💳</p>
                <h3 className="text-xl font-bold mb-2">Secure Payment</h3>
                <p className="text-gray-600">100% safe transactions</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl text-center">
                <p className="text-5xl mb-3">🔄</p>
                <h3 className="text-xl font-bold mb-2">Easy Returns</h3>
                <p className="text-gray-600">Hassle-free return policy</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl text-center">
                <p className="text-5xl mb-3">⭐</p>
                <h3 className="text-xl font-bold mb-2">Best Quality</h3>
                <p className="text-gray-600">Premium products only</p>
              </div>
            </div>
          </div>

          {/* QUICK NAVIGATION */}
          <div className="mb-10">
            <h2 className="text-3xl font-bold mb-6 text-center">⚡ Quick Links</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl cursor-pointer transition transform hover:scale-105" onClick={() => setCurrentPage('categories')}>
                <p className="text-4xl mb-3">📂</p>
                <h3 className="text-xl font-bold mb-2">Browse Categories</h3>
                <p className="text-gray-600">Explore different product categories</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl cursor-pointer transition transform hover:scale-105" onClick={() => setCurrentPage('categories')}>
                <p className="text-4xl mb-3">🔥</p>
                <h3 className="text-xl font-bold mb-2">Trending Now</h3>
                <p className="text-gray-600">Check out the latest trending products</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-xl cursor-pointer transition transform hover:scale-105" onClick={() => setCurrentPage('account')}>
                <p className="text-4xl mb-3">👤</p>
                <h3 className="text-xl font-bold mb-2">My Account</h3>
                <p className="text-gray-600">Manage your profile and orders</p>
              </div>
            </div>
          </div>

          {/* FEATURED PRODUCTS */}
          {products.length === 0 ? (
            <p className="text-center text-gray-500 text-xl">❌ Koi products nahi hain yet</p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {products.slice(0, 4).map(p => (
                <div key={p._id} className="bg-white p-4 rounded shadow-md hover:shadow-xl transition">
                  <img src={p.image} className="h-48 w-full object-contain rounded" alt={p.name} />
                  <h2 className="font-bold mt-3 text-lg truncate">{p.name}</h2>
                  <p className="text-gray-500 text-sm">{p.category || 'General'}</p>
                  <p className="text-green-600 font-bold text-xl mt-2">₹{p.price}</p>
                  <button onClick={() => handleAddToCart(p)} className="bg-orange-500 text-white w-full py-2 mt-3 rounded font-bold hover:bg-orange-600">🛒 Add to Cart</button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

  // ===== CATEGORIES PAGE =====
  if (currentPage === 'categories') {
    const categories = ['All', ...new Set(products.map(p => p.category || 'General'))];
    const filteredProducts = selectedCategory === 'All' ? products : products.filter(p => (p.category || 'General') === selectedCategory);

    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        <Navbar />
        <div className="p-10">
          <h2 className="text-3xl font-bold mb-6">📂 Browse Categories</h2>
          
          <div className="flex space-x-3 mb-8 overflow-x-auto pb-4">
            {categories.map(cat => (
              <button
                key={cat}
                onClick={() => setSelectedCategory(cat)}
                className={`px-6 py-2 rounded-full font-bold whitespace-nowrap ${selectedCategory === cat ? 'bg-blue-600 text-white' : 'bg-white text-blue-600 border-2 border-blue-600'}`}
              >
                {cat}
              </button>
            ))}
          </div>

          {filteredProducts.length === 0 ? (
            <p className="text-center text-gray-500 text-xl">❌ Koi products nahi hain is category mein</p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {filteredProducts.map(p => (
                <div key={p._id} className="bg-white p-4 rounded shadow-md hover:shadow-xl transition">
                  <img src={p.image} className="h-48 w-full object-contain rounded" alt={p.name} />
                  <h2 className="font-bold mt-3 text-lg truncate">{p.name}</h2>
                  <p className="text-gray-500 text-sm">{p.category || 'General'}</p>
                  <p className="text-green-600 font-bold text-xl mt-2">₹{p.price}</p>
                  <p className="text-sm text-gray-600 mb-2">Stock: {p.stock || 'Available'}</p>
                  <button onClick={() => handleAddToCart(p)} className="bg-orange-500 text-white w-full py-2 rounded font-bold hover:bg-orange-600">🛒 Add to Cart</button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

  // ===== CART PAGE =====
  if (currentPage === 'cart') {
    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        <Navbar />
        <div className="p-10 max-w-6xl mx-auto">
          <h2 className="text-3xl font-bold mb-6">🛒 Shopping Cart</h2>
          
          {cart.length === 0 ? (
            <div className="bg-white p-8 rounded-lg text-center">
              <p className="text-2xl text-gray-500 mb-4">Cart khali hai!</p>
              <button onClick={() => setCurrentPage('categories')} className="bg-blue-600 text-white px-6 py-3 rounded font-bold hover:bg-blue-700">
                🛍️ Shopping karo
              </button>
            </div>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              <div className="lg:col-span-2">
                {cart.map(item => (
                  <div key={item._id} className="bg-white p-6 rounded-lg shadow-md mb-4 flex justify-between items-center">
                    <div className="flex-1">
                      <h3 className="text-xl font-bold">{item.name}</h3>
                      <p className="text-gray-600">₹{item.price} each</p>
                    </div>
                    <div className="flex items-center space-x-4">
                      <button onClick={() => handleUpdateQuantity(item._id, item.quantity - 1)} className="bg-gray-300 px-3 py-1 rounded font-bold">-</button>
                      <span className="font-bold text-lg">{item.quantity}</span>
                      <button onClick={() => handleUpdateQuantity(item._id, item.quantity + 1)} className="bg-gray-300 px-3 py-1 rounded font-bold">+</button>
                      <p className="font-bold text-lg w-24 text-right">₹{item.price * item.quantity}</p>
                      <button onClick={() => handleRemoveFromCart(item._id)} className="bg-red-500 text-white px-4 py-2 rounded font-bold hover:bg-red-600">Remove</button>
                    </div>
                  </div>
                ))}
              </div>

              <div className="bg-white p-6 rounded-lg shadow-md h-fit sticky top-20">
                <h3 className="text-2xl font-bold mb-4">Order Summary</h3>
                <div className="space-y-3 border-b pb-4 mb-4">
                  <div className="flex justify-between">
                    <span>Total Items:</span>
                    <span className="font-bold">{cart.reduce((sum, item) => sum + item.quantity, 0)}</span>
                  </div>
                  <div className="flex justify-between text-xl font-bold text-green-600">
                    <span>Total:</span>
                    <span>₹{cartTotal}</span>
                  </div>
                </div>
                <button className="bg-orange-500 text-white w-full py-3 rounded font-bold hover:bg-orange-600 mb-2">💳 Checkout</button>
                <button onClick={() => setCurrentPage('categories')} className="bg-gray-300 text-gray-800 w-full py-2 rounded font-bold hover:bg-gray-400">Continue Shopping</button>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }

  // ===== ACCOUNT PAGE =====
  if (currentPage === 'account') {
    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        <Navbar />
        <div className="p-10 max-w-2xl mx-auto">
          <h2 className="text-3xl font-bold mb-6">👤 My Account</h2>
          
          <div className="bg-white p-8 rounded-lg shadow-md mb-6">
            <h3 className="text-2xl font-bold mb-4">Profile Information</h3>
            <div className="space-y-4">
              <div className="flex justify-between border-b pb-3">
                <span className="font-bold">Name:</span>
                <span>{user?.name}</span>
              </div>
              <div className="flex justify-between border-b pb-3">
                <span className="font-bold">Email:</span>
                <span>{user?.email}</span>
              </div>
              <div className="flex justify-between border-b pb-3">
                <span className="font-bold">Role:</span>
                <span className="bg-blue-100 text-blue-700 px-3 py-1 rounded font-bold">{user?.role || 'User'}</span>
              </div>
              <div className="flex justify-between">
                <span className="font-bold">Member Since:</span>
                <span>2024</span>
              </div>
            </div>
          </div>

          <div className="bg-white p-8 rounded-lg shadow-md">
            <h3 className="text-2xl font-bold mb-4">Account Settings</h3>
            <button className="w-full bg-gray-300 text-gray-800 py-2 rounded font-bold hover:bg-gray-400 mb-2">✏️ Edit Profile</button>
            <button className="w-full bg-gray-300 text-gray-800 py-2 rounded font-bold hover:bg-gray-400 mb-2">🔑 Change Password</button>
            <button className="w-full bg-red-500 text-white py-2 rounded font-bold hover:bg-red-600">🚪 Logout</button>
          </div>
        </div>
      </div>
    );
  }

  // ===== SHOP PAGE =====
  if (currentPage === 'shop') {
    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        <Navbar />
        <div className="p-10">
          <h2 className="text-3xl font-bold mb-6">🎉 All Products</h2>
          {products.length === 0 ? (
            <p className="text-center text-gray-500 text-xl">❌ Koi products nahi hain</p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
              {products.map(p => (
                <div key={p._id} className="bg-white p-4 rounded shadow-md hover:shadow-xl transition">
                  <img src={p.image} className="h-48 w-full object-contain rounded" alt={p.name} />
                  <h2 className="font-bold mt-3 text-lg truncate">{p.name}</h2>
                  <p className="text-gray-500 text-sm">{p.category || 'General'}</p>
                  <p className="text-green-600 font-bold text-xl mt-2">₹{p.price}</p>
                  <button onClick={() => handleAddToCart(p)} className="bg-orange-500 text-white w-full py-2 mt-3 rounded font-bold hover:bg-orange-600">🛒 Add to Cart</button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

  // ===== ADMIN PAGE =====
  if (currentPage === 'admin') {
    return (
      <div className="bg-gray-100 min-h-screen font-sans">
        <Navbar />
        <div className="p-10 flex flex-col lg:flex-row gap-10">
          <div className="max-w-lg bg-white p-6 shadow-lg rounded-lg">
            <h2 className="text-2xl font-bold mb-6">➕ Add New Product</h2>
            <div className="space-y-4">
              <input placeholder="Product Name" className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600" value={form.name} onChange={e => setForm({...form, name: e.target.value})} />
              <input placeholder="Price (₹)" type="number" className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600" value={form.price} onChange={e => setForm({...form, price: e.target.value})} />
              <input placeholder="Image URL" className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600" value={form.image} onChange={e => setForm({...form, image: e.target.value})} />
              <input placeholder="Category" className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600" value={form.category} onChange={e => setForm({...form, category: e.target.value})} />
              <input placeholder="Stock" type="number" className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:border-blue-600" value={form.stock} onChange={e => setForm({...form, stock: e.target.value})} />
              <button onClick={handleAdd} disabled={loading} className="bg-blue-600 text-white w-full py-3 font-bold rounded hover:bg-blue-700 disabled:bg-gray-500">{loading ? "⏳ Adding..." : "✅ UPLOAD PRODUCT"}</button>
            </div>
          </div>

          <div className="flex-1">
            <h2 className="text-2xl font-bold mb-6">📦 All Products ({products.length})</h2>
            {products.length === 0 ? (
              <p className="text-gray-500 text-lg">Koi products nahi hain</p>
            ) : (
              <div className="grid grid-cols-1 gap-4">
                {products.map(p => (
                  <div key={p._id} className="bg-white p-4 rounded shadow-md flex justify-between items-center">
                    <div className="flex-1">
                      <h3 className="font-bold text-lg">{p.name}</h3>
                      <p className="text-green-600 font-bold">₹{p.price}</p>
                      <p className="text-sm text-gray-600">{p.category || 'General'} | Stock: {p.stock || 'N/A'}</p>
                    </div>
                    <button onClick={() => handleDelete(p._id)} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 font-bold">🗑️ Delete</button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
